import { StatusBar } from 'expo-status-bar';
import React,{useEffect,useState} from 'react';
import { Button,Image,StyleSheet, Text, View,Platform ,AsyncStorage} from 'react-native';
import * as imagePicker from "expo-image-picker"
export default function App() {
const [image,setImage]=useState()
    useEffect(()=>{
console.log(imagePicker)
        const {status}=  imagePicker.requestMediaLibraryPermissionsAsync().then((response=>{
            console.log(response)
        }))
       if(status !=="granted") {
           alert("not allow")
       }
    },[])
 async function uploadImage(){
        const option={}
        let result= await imagePicker.launchImageLibraryAsync({
            mediaTypes:imagePicker.MediaTypeOptions.All,
            allowsEditing:true,
            aspect:[4,3],
            quality:1

        })
      await  AsyncStorage.setItem("img",result.uri)
     console.log(result.uri)
     if(!result.cancelled){
         setImage(result.uri)
     }
  }


  return (
    <View style={styles.container}>
      {/*<Text>Open up App.js to start working on your app!</Text>*/}
      <Button title="Upload Photo" onPress={uploadImage}/>
        <Button title="Saving" onPress={testing} />
        <Button title="load" onPress={getItem} />
      <StatusBar style="auto" />
        {image && <Image source={{uri:image}} style={{
            width:200,
            height:200

        }}/>}
    </View>
  );
}

async function getItem(){
    try {
      let e  = await AsyncStorage.getItem("ah")
       await console.log(e)
    }catch (error){
        console.log("Erooooooooor")
    }

}
async function testing(){
    try {
        await AsyncStorage.setItem("ah","test123")
    }catch (error){
        console.log("Erooooooooor")
    }

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
